/*
 * Input Influx DB message format:
 * Based on: SELECT mean("temperature") FROM "environment" WHERE time > now() - 7d GROUP BY time(30m) fill(null) tz('Europe/Bratislava')
 * --------------------------------------------------------------
 * [ ...
 * {"time":"2025-11-09T18:00:00.000Z","mean":null},
 * {"time":"2025-11-09T18:30:00.000Z","mean":null},
 * {"time":"2025-11-09T19:00:00.000Z","mean":21.964823529411788},
 * {"time":"2025-11-09T19:30:00.000Z","mean":21.859259259259243},
 * {"time":"2025-11-09T20:00:00.000Z","mean":21.799594594594634},
 *   ... ]
 * 
 * Output data format for Chart Node:
 * --------------------------------------------------------------
 * msg.topic = "Temperature"
 * msg.payload = 
 * [
 *      {"x":1762714800000,"y":21.965},
 *      {"x":1762716600000,"y":21.859},
 *      {"x":1762718400000,"y":21.8},
 *      ...
 * ]
 * */

// Array of objects in msg.payload
const data = msg.payload;

// Name of series for chart (for legend)
const seriesName = "Temperature"; // 'Show Legend' must be enabled on Chart Node

// Data conversion to output format:
const chartData = data
    // 1. Filtration - remove data points with 'null' value
    // (to avoid empty space in graph-line of the Chart node)
    .filter(item => item.mean !== null && item.mean !== undefined)

    // 2. Mapping
    .map(item => {
        // Timestamp in mili-sec
        const timestamp_ms = new Date(item.time).getTime();

        // Rounding the value of 'mean' to 3 decimal places
        const roundedMean = parseFloat(item.mean.toFixed(3)); 

        // Creating an object for a chart
        return {
            //"series" : seriesName, - instead the 'msg.topic' is used
            "x": timestamp_ms,
            "y": roundedMean 
        };
    });

// Final object for Chart node
msg.payload = chartData
msg.topic = seriesName

return msg;

/*
 * Input MQTT message format (msg.payload):
 * -----------------------------------------
 * {
 *   "home/thermometer/temperature":
 *      {
 *          "value":24.06, 
 *          "unit":"C"
 *      },
 *   "home/thermometer/humidity":
 *      {
 *          "value":64.98,
 *          "unit":"%"
 *      }
 * }
 * 
 * Output message format (InfluxDB):
 * -----------------------------------------
 * msg.measurement = measurementName;
 * msg.payload = 
 * [
 *      { fields },
 *      { tags   }
 * ];
 */

const payloadIn = msg.payload;

/* Define field and tag names */
const measurementName = "environment";
const tempTopic = "home/thermometer/temperature";
const humiTopic = "home/thermometer/humidity";
const deviceTag = "home/thermometer/hall";

// Helper function to safely extract the 'value' field and convert it to a float.
function extractAndParse(topic) {
    const data = payloadIn[topic];

    // 1. Check if the object for the topic exists
    if (!data || data.value === null || data.value === undefined) {
        // If data is missing or value is null/undefined, return null
        return null;
    }

    // 2. Access the nested 'value' key and attempt conversion
    const value = data.value;

    // parseFloat is usually safe, but check for non-numeric input for robustness
    if (typeof value === 'number') {
        return value;
    }

    const result = parseFloat(value);

    // Check if the result is NaN (Not a Number)
    if (isNaN(result)) {
        node.warn(`Warning: Could not parse numeric value for topic: ${topic}. Value was: ${value}`);
        return null;
    }

    return result;
}

// Structure for writing to InfluxDB
msg.measurement = measurementName;
msg.payload = [
    {
        // Correct access path: payloadIn[topic].value
        temperature: extractAndParse(tempTopic),
        humidity: extractAndParse(humiTopic)
    },
    {
        device: deviceTag
    }
];

// Log the final payload for debugging. Check the Node-RED debug tab.
// node.warn(JSON.stringify(msg.payload));

return msg;

